SELECT UPPER(db_apoplavs.user_card.last_name) as 'NAME', db_apoplavs.user_card.first_name, db_apoplavs.subscription.price FROM db_apoplavs.member INNER JOIN db_apoplavs.user_card ON db_apoplavs.user_card.id_user = db_apoplavs.member.id_user_card INNER JOIN db_apoplavs.subscription ON db_apoplavs.subscription.id_sub = db_apoplavs.member.id_user_card WHERE db_apoplavs.subscription.price > 42 ORDER BY db_apoplavs.user_card.last_name, db_apoplavs.user_card.first_name;